'use strict';

$(function () {

  // Magnific Popup
  // $('.popup-with-move-anim').magnificPopup({
  $('.popup-with-zoom-anim').magnificPopup({
    type: 'inline',

    fixedContentPos: false,
    fixedBgPos: true,

    overflowY: 'auto',

    closeBtnInside: true,
    preloader: false,

    midClick: true,
    removalDelay: 300,
    // mainClass: 'my-mfp-slide-bottom'
    mainClass: 'my-mfp-zoom-in'
  });
});

'use strict';

$(function () {

  function eventCheck(e) {
    var container = $('.top-menu ul');

    if (!container.is(e.target) && container.has(e.target).length === 0 && !$(e.target).hasClass('hamburger') && !$(e.target).hasClass('hamburger__btn')) {
      container.fadeOut();

      $('.hamburger').find('.hamburger__btn').removeClass('hamburger__btn_on');
    }
  }

  function regulationsForMq(mq) {
    if (mq.matches) {
      $('.hamburger').click(function () {
        $(this).find('.hamburger__btn').toggleClass('hamburger__btn_on');

        $('.top-menu ul').slideToggle();

        return false;
      });

      document.addEventListener('touchstart', eventCheck, false);
      document.addEventListener('click', eventCheck, false);
    }
  }

  var mq = window.matchMedia('screen and (max-width: 992px)');

  mq.addListener(regulationsForMq);

  regulationsForMq(mq);
});

'use strict';

$(function () {

  $('.catalog__btn-more').click(function () {
    $('.catalog ul').addClass('catalog-open');

    $(this).css('display', 'none');
    return false;
  });
});

'use strict';

// initMap() - функция инициализации карты
function initMap() {
  // Координаты центра на карте. Широта: 59.960541, Долгота: 30.445356
  var centerLatLng = new google.maps.LatLng(59.960541, 30.445356);

  // Обязательные опции с которыми будет проинициализированна карта
  var mapOptions = {
    center: centerLatLng, // Координаты центра мы берем из переменной centerLatLng
    zoom: 15 // Зум по умолчанию. Возможные значения от 0 до 21
  };

  // Создаем карту внутри элемента #map
  var map = new google.maps.Map(document.getElementById('map'), mapOptions);

  // Добавляем маркер
  var marker = new google.maps.Marker({
    position: centerLatLng, // Координаты расположения маркера. В данном случае координаты нашего маркера совпадают с центром карты, но разумеется нам никто не мешает создать отдельную переменную и туда поместить другие координаты.
    map: map, // Карта на которую нужно добавить маркер
    title: 'ШАССИК - разборка иномарок' // (Необязательно) Текст выводимый в момент наведения на маркер
  });
}

// Ждем полной загрузки страницы, после этого запускаем initMap()
google.maps.event.addDomListener(window, 'load', initMap);